import React, { useState, useEffect } from 'react';
import '../CustomerDashboard.css'; // Ensure CSS file is linked

const CustomerDashboard = () => {
  const [customer, setCustomer] = useState(null);
  const [devices, setDevices] = useState([]);
  const [locations, setLocations] = useState([]);

  // Placeholder function to navigate to the energy consumption details page
  const viewEnergyDetails = () => {
    console.log('Navigate to the energy consumption details page');
    // Implement navigation logic here
  };

  // Fetch customer, device, and location data from backend
  useEffect(() => {
    // Fetch customer details
    fetch('http://localhost:8080/customers/1') // Update with the correct endpoint if necessary
      .then(response => response.json())
      .then(data => {
        setCustomer(data);
      })
      .catch(error => console.error('Error:', error));

    // Fetch devices associated with the customer
    fetch('http://localhost:8080/devices/1') // Assuming this endpoint returns all devices
      .then(response => response.json())
      .then(allDevices => {
        const customerDevices = allDevices.filter(device => device.customerId === 1);
        setDevices(customerDevices);
      })
      .catch(error => console.error('Error:', error));

    // Fetch locations
    fetch('http://localhost:8080/locations/1') // Assuming this endpoint returns all locations
      .then(response => response.json())
      .then(allLocations => {
        // Assuming you have a way to filter locations for a specific customer
        const customerLocations = allLocations.filter(location => location.customerId === 1); // Update this condition based on your data model
        setLocations(customerLocations);
      })
      .catch(error => console.error('Error:', error));
  }, []);

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>Customer Details</h1>
      </header>
      <main className="dashboard-content">
        <section className="customer-details">
          <h2>Customer Details</h2>
          {customer && (
            <div>
              <p>First Name: {customer.firstName}</p>
              <p>Last Name: {customer.lastName}</p>
              <p>Address: {customer.address}</p>
              <p>Email: {customer.email}</p>
              <p>Phone: {customer.phone}</p>
            </div>
          )}
        </section>
        <section className="devices-list">
          <h2>Devices</h2>
          {devices.map(device => (
            <div key={device.deviceId}>
              <p>Type: {device.type}</p>
              <p>Model: {device.model}</p>
              <p>Manufacturer: {device.manufacturer}</p>
              <p>Status: {device.status}</p>
            </div>
          ))}
        </section>
        <section className="service-locations">
          <h2>Service Locations</h2>
          {locations.map(location => (
            <div key={location.locationId}>
              <p>Address: {location.address}</p>
              <p>Zip Code: {location.zipCode}</p>
              <p>Square Footage: {location.squareFootage}</p>
            </div>
          ))}
        </section>
        <button className="view-details-btn" onClick={viewEnergyDetails}>
          View Energy Consumption Details
        </button>
      </main>
    </div>
  );
};

export default CustomerDashboard;
