import React, { useState } from 'react';
import '../LoginPage.css'; // Ensure you have the CSS file

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (event) => {
    event.preventDefault();
    // Handle login logic here
    console.log('Email:', email, 'Password:', password);
    // Redirect to dashboard or show an error message
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2>Welcome to SHEMS</h2>
        <p>Please Sign In</p>
        <form onSubmit={handleLogin} className="login-form">
          <div className="input-group">
            <label htmlFor="email">Username</label>
            <input
              id="email"
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email or username"
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">PASSWORD</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
            <a href="/forgot-password" className="forgot-password">
              Forgot Password?
            </a>
          </div>
          <div className="form-actions">
            <button type="submit" className="btn-sign-in">Sign in</button>
          </div>
        </form>
        <p className="signup-text">
          New on our platform? <a href="/register">Create an account</a>
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
