import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import '../CustomerDashboard.css'; // Ensure CSS file is linked

const CustomerDashboard = () => {
  const [customer, setCustomer] = useState(null);
  const [devices, setDevices] = useState([]);
  const [locations, setLocations] = useState([]);
  const history = useHistory(); // Hook to access the history instance

  // Function to navigate to the energy consumption details page
  const viewEnergyDetails = () => {
    history.push('/energydetails'); // Route to navigate to
  };

  // Fetch customer, device, and location data from backend
  useEffect(() => {
    // Fetch customer details
    fetch('http://localhost:8080/customers/1')
      .then(response => response.json())
      .then(data => {
        setCustomer(data);
      })
      .catch(error => console.error('Error:', error));

    // Fetch devices associated with the customer
    fetch('http://localhost:8080/devices')
      .then(response => response.json())
      .then(allDevices => {
        const customerDevices = allDevices.filter(device => device.customerId === 1);
        setDevices(customerDevices);
      })
      .catch(error => console.error('Error:', error));

    // Fetch locations
    fetch('http://localhost:8080/locations')
      .then(response => response.json())
      .then(allLocations => {
        const customerLocations = allLocations.filter(location => location.customerId === 1);
        setLocations(customerLocations);
      })
      .catch(error => console.error('Error:', error));
  }, []);

  return (
    <div className="dashboard">
      {/* ...rest of your component */}
      <button className="view-details-btn" onClick={viewEnergyDetails}>
        View Energy Consumption Details
      </button>
    </div>
  );
};

export default CustomerDashboard;
