import React from 'react';
import { useHistory } from 'react-router-dom';

const CustomerDashboard = () => {
  const history = useHistory();

  // Example data (in real application, this data would come from a server)
  const customerDetails = {
    name: 'John Doe',
    email: 'john@example.com',
    id: '123456'
  };

  const devices = [
    { id: 1, name: 'Smart Refrigerator', model: 'Bosch 800' },
    { id: 2, name: 'Smart Light Bulb', model: 'IKEA' },
    // ... other devices
  ];

  const serviceLocations = [
    { id: 1, address: '123 Main St, Townsville' },
    { id: 2, address: '456 Side St, Villagetown' },
    // ... other locations
  ];

  const navigateToEnergyDetails = () => {
    // Navigate to the energy consumption details page
    history.push('/energy-consumption-details');
  };

  return (
    <div>
      <h1>Customer Dashboard</h1>
      <h2>Customer Details</h2>
      <p>Name: {customerDetails.name}</p>
      <p>Email: {customerDetails.email}</p>
      <p>ID: {customerDetails.id}</p>

      <h2>Smart Devices</h2>
      <ul>
        {devices.map(device => (
          <li key={device.id}>{device.name} - {device.model}</li>
        ))}
      </ul>

      <h2>Service Locations</h2>
      <ul>
        {serviceLocations.map(location => (
          <li key={location.id}>{location.address}</li>
        ))}
      </ul>

      <button onClick={navigateToEnergyDetails}>
        View Energy Consumption Details
      </button>
    </div>
  );
};

export default CustomerDashboard;
