import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../LoginPage.css'; // Make sure this path is correct

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (event) => {
    event.preventDefault();
    // TODO: Implement your login logic here
    // Dummy login success condition
    const loginSuccess = true; // Replace with actual login check

    if (loginSuccess) {
      // If login is successful, navigate to the dashboard
      navigate('/dashboard');
    } else {
      // If login fails, handle accordingly
      console.error('Login failed');
      // Show an error message to the user
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2>Welcome to SHEMS</h2>
        <p>Please Sign In</p>
        <form onSubmit={handleLogin} className="login-form">
          <div className="input-group">
            <label htmlFor="email">Email or Username</label>
            <input
              id="email"
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email or username"
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>
          <div className="forgot-password">
            <Link to="/forgot-password">Forgot Password?</Link>
          </div>
          <div className="form-actions">
            <button type="submit" className="btn-sign-in">Sign in</button>
          </div>
        </form>
        <p className="signup-text">
          New on our platform? <Link to="/register">Create an account</Link>
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
