import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
import LoginPage from './components/LoginPage';

function App() {
  return (
    <Router>
      <div className="app">
        <nav className="navbar">
          <div className="logo">Logo</div>
          <div className="menu">
            <Link to="/about" className="btn-demo">ABOUT US</Link>
          </div>
        </nav>

        {/* Use Routes instead of Switch for react-router-dom v6 */}
        <Routes>
          {/* Route for the homepage */}
          <Route exact path="/" element={
            <header className="header">
              <div className="header-content">
                <h1>THE EASIEST WAY TO ANALYZE YOUR ENERGY CONSUMPTION</h1>
                {/* Link to the login page */}
                <Link to="/login" className="btn-header-demo">GET SMARTER</Link>
              </div>
            </header>
          }/>

          {/* Route for the login page */}
          <Route path="/login" element={<LoginPage />} />

          {/* Route for the about page */}
          {/* Add your about page component here in the element prop */}
          <Route path="/about" element={<div>About Us</div>} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
